<?php	require_once 'smarty_inc.php';	require_once '../model/MessageModel.class.php';	require_once '../model/FenyePage.class.php';	$emp_name = @$_POST['emp_name'];	$emp_pwd = @$_POST['emp_pwd'];	//到数据库去验证	if($emp_name == "张三丰" && $emp_pwd == "123"){				//把登录人的名字（对象），存放在session中		session_start();		$_SESSION['login_name'] = $emp_name; 				//直接用模板显示		//调用MessageModel里面的方法		$messageModel = new MessageModel();		//$smarty->assign("res",$messageModel->showMessage($emp_name));		//创建一个$fenyePage对象实例		$fenyePage = new FenyePage();		$fenyePage->gotoUrl = "MessageListUIController.php";				$messageModel->showMessageByPage($fenyePage,$emp_name);		//这里我们需要把分页获取的信息分配给messageList.tpl模板文件		$smarty->assign("res",$fenyePage->res_array);		$smarty->assign("navi",$fenyePage->navigate);		$smarty->assign("pageCount",$fenyePage->pageCount);		$smarty->display("messageList.tpl");			}else{					}?>